def sqrt(x):
    if x < 0:
        return -1
    if x == 0 or x == 1:
        return x
    low = 0
    high = x // 2
    while low <= high:
        mid = low + (high - low) // 2
        sqr = mid * mid
        if sqr == x:
            return mid
        if sqr < x:
            low = mid + 1
        else:
            high = mid - 1
    return high
