package com.algos.puzzle.gameoflife;

import java.util.ArrayList;
import java.util.List;
import java.util.Random;

public class ConwayLife {
    private List<List<Cell>> grid;

    public ConwayLife(int rows, int cols) {
        this.grid = new ArrayList<>();
        for (int i = 0; i < rows; i++) {
            this.grid.add(new ArrayList<>());
            for (int j = 0; j < cols; j++) {
                this.grid.get(i).add(new Cell(false, i, j));
            }
        }
    }

    public void populate(double probability) {
        Random random = new Random();
        for (List<Cell> row : grid) {
            for (Cell cell : row) {
                cell.setAlive(random.nextDouble() < probability);
            }
        }
    }

    public void printGrid() {
        for (List<Cell> row : grid) {
            for (Cell cell : row) {
                System.out.print(cell.isAlive() ? "O" : " ");
            }
            System.out.println();
        }
    }

    public void nextGeneration() {
        for (int row = 0; row < grid.size(); row++) {
            for (int col = 0; col < grid.get(row).size(); col++) {
                Cell cell = grid.get(row).get(col);
                int neighbours = getNeighbours(row, col);
                cell.setNeighbours(neighbours);
                if (cell.isAlive()) {
                    if (neighbours < 2 || neighbours > 3) {
                        cell.setAlive(false);
                    }
                } else {
                    if (neighbours == 3) {
                        cell.setAlive(true);
                    }
                }
            }
        }
    }

    private int getNeighbours(int row, int col) {
        int count = 0;
        for (int i = row - 1; i <= row + 1; i++) {
            for (int j = col - 1; j <= col + 1; j++) {
